class Solution:
    def findPairs(self, nums: List[int], k: int) -> int:
        count = 0
        seen = set()
        for i in range(len(nums)):
            for j in range(i + 1, len(nums)):
                if abs(nums[i] - nums[j]) == k:
                    pair = tuple(sorted((nums[i], nums[j])))
                    if pair not in seen:
                        count += 1
                        seen.add(pair)
        return count
