                return 0

            if (left, right) in dp:
                return dp[(left, right)]

            pick = slices[left]
            alice_left, alice_right = left + 1, right
            bob_left, bob_right = left, right - 1

            ans = pick + max(solve(alice_left, bob_right), solve(bob_left, alice_right))

            dp[(left, right)] = ans
            return ans

        return solve(0, n - 1)
