def sort(words):
    """Sorts a massive list of strings in caseless, lexicographic order using a generator.

    Args:
        words: A generator that yields strings.

    Returns:
        A generator that yields the strings in lexicographic order.
    """
    if not words:
        yield []
        return

    buckets = {}
    for word in words:
        key = word.lower()
        if key not in buckets:
            buckets[key] = []
        buckets[key].append(word)

    sorted_keys = sorted(buckets.keys())

    for key in sorted_keys:
        for word in buckets[key]:
            yield word
