class Solution:
    def nearestPalindromic(self, n: str) -> str:
        k = len(n)
        num = int(n)
        candidates = [str(10**(k-1)-1), str(10**k+1)]
        prefix = int(n[:(k+1)//2])
        for p in [prefix-1, prefix, prefix+1]:
            p_str = str(p)
            if k % 2 == 0:
                candidate = p_str + p_str[::-1]
            else:
                candidate = p_str + p_str[:-1][::-1]
            candidates.append(candidate)

        res = ""
        min_diff = float('inf')
        for candidate in candidates:
            if candidate == n:
                continue
            diff = abs(int(candidate) - num)
            if diff < min_diff:
                min_diff = diff
                res = candidate
            elif diff == min_diff:
                if int(candidate) < int(res):
                    res = candidate
        return res
