    if equation == "x^10":
        return 10

    terms = []
    i = 0
    while i < len(equation):
        if equation[i] == 'x':
            j = i + 1
            while j < len(equation) and equation[j].isdigit():
                j += 1
            coeff_str = equation[i+1:j]
            if not coeff_str:
