def differentiate(equation, point):
    """
    Differentiates a polynomial for a given value of x.

    Args:
        equation (str): The polynomial as a string.
        point (int): The point to evaluate the equation at.

    Returns:
        int: The derivative of the polynomial at the given point.
    """
    equation = equation.replace(" ", "")
    terms = equation.split("+")
    derivative = 0
    for term in terms:
        if term == "":
            continue
        if "x" not in term:
            derivative += int(term)
        else:
            if term == "x":
                derivative += 1
            elif term == "-x":
                derivative -= 1
            else:
                try:
                    coeff = int(term[:-1])
                    power = int(term[-1:])
                    if power == 0:
                        derivative += coeff
                    elif power == 1:
                        derivative += coeff * 1
                    else:
                        derivative += coeff * (power - 1)
                except ValueError:
                    pass

    return derivative
