        self.left = left
        self.right = right

class Solution:
    def verticalTraversal(self, root: TreeNode) -> List[List[int]]:
        if not root:
            return []

        column_map = {}

        def dfs(node, x, y):
            if not node:
                return

            if x not in column_map:
                column_map[x] = []
            column_map[x].append((y, node.val))

            dfs(node.left, x - 1, y - 1)
            dfs(node.right, x + 1, y - 1)

        dfs(root, 0, 0)

        sorted_cols = sorted(column_map.keys())
        result = []

        for col in sorted_cols:
            column_map[col].sort(key=lambda x: x[0])  # Sort by y-coordinate
            values = [val for _, val in column_map[col]]
            result.append(values)

        return result
