class Solution:
    def widthOfBinaryTree(self, root: TreeNode) -> int:
        if not root:
            return 0

        queue = [(root, 0)]
        min_level = 0
        max_level_width = 0

        while queue:
            node, level = queue.pop(0)

            if level > min_level:
                max_level_width = max(max_level_width, len(queue))

            if node.left:
