def ds_multof_pfs(n_min, n_max):
    """
    Finds numbers within a range that satisfy the property:
    The sum of divisors of a number is divisible by the sum of its prime factors.

    Args:
        n_min (int): The lower limit (inclusive).
        n_max (int): The upper limit (inclusive).

    Returns:
        list: A sorted list of numbers that fulfill the property.
    """
    result = []
    for n in range(n_min, n_max + 1):
        prime_factors = []
        temp_n = n
        i = 2
        while i * i <= temp_n:
            if temp_n % i == 0:
                prime_factors.append(i)
                while temp_n % i == 0:
                    temp_n //= i
            i += 1
        if temp_n > 1:
            prime_factors.append(temp_n)

        prime_factors_sum = sum(prime_factors)

        divisors_sum = 0
        for i in range(1, n + 1):
            if n % i == 0:
                divisors_sum += i

        if prime_factors_sum != 0 and divisors_sum % prime_factors_sum == 0:
            result.append(n)
    return sorted(result)
