def peaceful_yard(yard, min_distance):
    """
    Determines whether the distances between visiting cats are at least the minimum distance.

    Args:
        yard: A list of strings representing the yard as a grid.
        min_distance: The minimum distance needed to prevent problems.

    Returns:
        True if the distances are sufficient for a peaceful afternoon, False otherwise.
    """

    cat_positions = {}
    for row_index, row in enumerate(yard):
        for col_index, cell in enumerate(row):
            if cell == 'L':
                cat_positions['L'] = (row_index, col_index)
            elif cell == 'M':
                cat_positions['M'] = (row_index, col_index)
            elif cell == 'R':
                cat_positions['R'] = (row_index, col_index)

    cats = list(cat_positions.keys())

    if len(cats) <= 1:
        return True

    if len(cats) == 2:
        dist = math.sqrt((cat_positions[cats[0]][0] - cat_positions[cats[1]][0])**2 + (cat_positions[cats[0]][1] - cat_positions[cats[1]][1])**2)
        return dist >= min_distance

    if len(cats) == 3:
        distances = []
        distances.append(math.sqrt((cat_positions[cats[0]][0] - cat_positions[cats[1]][0])**2 + (cat_positions[cats[0]][1] - cat_positions[cats[1]][1])**2))
        distances.append(math.sqrt((cat_positions[cats[0]][0] - cat_positions[cats[2]][0])**2 + (cat_positions[cats[0]][1] - cat_positions[cats[2]][1])**2))
        distances.append(math.sqrt((cat_positions[cats[1]][0] - cat_positions[cats[2]][0])**2 + (cat_positions[cats[1]][1] - cat_positions[cats[2]][1])**2))

        for distance in distances:
            if distance < min_distance:
                return False
        return True

    return False
