import math

def peaceful_yard(yard, min_distance):
	"""
	Determines whether the distances between visiting cats are at least the minimum distance.

	Args:
		yard: A list of strings representing the yard as a grid.
		min_distance: The minimum distance needed to prevent problems.

	Returns:
		True if the distances between all cats are at least the minimum distance, False otherwise.
	"""
	cat_positions = {}
	for row in range(len(yard)):
		for col in range(len(yard[row])):
			if yard[row][col] in ('L', 'M', 'R'):
				if yard[row][col] not in cat_positions:
					cat_positions[yard[row][col]] = (row, col)

	if len(cat_positions) <= 1:
		return True

	for cat1, pos1 in cat_positions.items():
		for cat2, pos2 in cat_positions.items():
			if cat1 != cat2:
				distance = math.sqrt((pos1[0] - pos2[0])**2 + (pos1[1] - pos2[1])**2)
				if distance < min_distance:
					return False

	return True
