def duplicate_encode(word):
	"""Encodes a string where each character is '(' if it appears only once, and ')' if it appears more than once.

	Args:
		word (str): The input string.

	Returns:
		str: The encoded string.
	"""
	word = word.lower()
	encoded_word = ""
	char_counts = {}
	for char in word:
		char_counts[char] = char_counts.get(char, 0) + 1

	for char in word:
		if char_counts[char] == 1:
			encoded_word += "("
		else:
			encoded_word += ")"

	return encoded_word
