class Solution:
    def numberToWords(self, num: int) -> str:
        """
        Convert a non-negative integer to its english words representation.
        Given input is guaranteed to be less than 231 - 1.
        """

        if num == 0:
            return "Zero"

        ones = ["", "One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine", "Ten", "Eleven", "Twelve", "Thirteen", "Fourteen", "Fifteen", "Sixteen", "Seventeen", "Eighteen", "Nineteen"]
        tens = ["", "", "Twenty", "Thirty", "Forty", "Fifty", "Sixty", "Seventy", "Eighty", "Ninety"]
        thousands = ["", "Thousand", "Million", "Billion"]

        def convert_to_words(n):
            if n == 0:
                return ""
            elif n < 20:
                return ones[n]
            elif n < 100:
                return tens[n // 10] + (" " + ones[n % 10] if n % 10 != 0 else "")
            elif n < 1000:
                return ones[n // 100] + " Hundred " + convert_to_words(n % 100)
            elif n < 1000000:
                return convert_to_words(n // 1000) + " Thousand " + convert_to_words(n % 1000)
            elif n < 1000000000:
                return convert_to_words(n // 1000000) + " Million " + convert_to_words(n % 1000000)
            else:
                return convert_to_words(n // 1000000000) + " Billion " + convert_to_words(n % 1000000000)

        result = convert_to_words(num)
        return result.strip()
