    def is_valid(self, number):
        digit_sum = sum(int(digit) for digit in str(number))
        if digit_sum == 0:
            return False
        return number % digit_sum == 0

    def get_next(self, n):
        n += 1
        while True:
            if self.is_valid(n):
                return n
            n += 1

    def get_series(self, n, start=0):
        series = []
        num = start
        while len(series) < n:
            if self.is_valid(num):
                series.append(num)
            num += 1
        return series
