class Solution:
    def longestCommonSubsequence(self, text1: str, text2: str) -> int:
        n = len(text1)
        m = len(text2)

        # Initialize a 2D array to store the lengths of common subsequences
        dp = [[0] * (m + 1) for _ in range(n + 1)]

        # Iterate through the strings to populate the dp array
        for i in range(1, n + 1):
            for j in range(1, m + 1):
                if text1[i - 1] == text2[j - 1]:
                    # If the characters match, increment the length of the common subsequence
                    dp[i][j] = dp[i - 1][j - 1] + 1
                else:
                    # If the characters don't match, take the maximum length from the previous row or column
                    dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

        # The length of the longest common subsequence is stored in the bottom-right corner of the dp array
        return dp[n][m]

