def sort_string(s, ordering):
    """Sorts the first string based on the second string's ordering.

    Args:
        s: The string to be sorted.
        ordering: The string defining the ordering.

    Returns:
        The sorted string.
    """
    ordered_chars = []
    remaining_chars = []

    for char in ordering:
        if char in s:
            ordered_chars.append(char)

    for char in s:
        if char not in ordering:
            remaining_chars.append(char)

    ordered_chars.sort()
    return "".join(ordered_chars) + "".join(remaining_chars)
