class Vector:
    def __init__(self, *args):
        if len(args) == 3:
            self.x, self.y, self.z = args
        elif len(args) == 1:
            if isinstance(args[0], tuple) and len(args[0]) == 3:
                self.x, self.y, self.z = args[0]
            else:
                raise ValueError("Invalid input format for Vector constructor.")
        else:
            raise ValueError("Invalid input format for Vector constructor.")

    def __add__(self, other):
        if not isinstance(other, Vector):
            raise TypeError("Can only add Vector to Vector")
        return Vector(self.x + other.x, self.y + other.y, self.z + other.z)

    def __sub__(self, other):
        if not isinstance(other, Vector):
            raise TypeError("Can only subtract Vector from Vector")
        return Vector(self.x - other.x, self.y - other.y, self.z - other.z)

    def __eq__(self, other):
        if not isinstance(other, Vector):
            return False
        return math.isclose(self.magnitude, other.magnitude) and self.direction == other.direction

    def cross(self, other):
        if not isinstance(other, Vector):
            raise TypeError("Can only compute cross product of Vector with Vector")
        x = self.y * other.z - self.z * other.y
        y = self.z * other.x - self.x * other.z
        z = self.x * other.y - self.y * other.x
        return Vector(x, y, z)

    def dot(self, other):
        if not isinstance(other, Vector):
            raise TypeError("Can only compute dot product of Vector with Vector")
        return self.x * other.x + self.y * other.y + self.z * other.z

    def to_tuple(self):
        return (self.x, self.y, self.z)

    def __str__(self):
        return f"Vector({self.x}, {self.y}, {self.z})"

    def magnitude(self):
        return math.sqrt(self.x**2 + self.y**2 + self.z**2)

    def x(self):
