class Solution:
    def distinctSubseqII(self, S: str) -> int:
        MOD = 10**9 + 7
        n = len(S)
        dp = [0] * (n + 1)
        last = {}
        dp[0] = 1
        for i in range(1, n + 1):
            dp[i] = (2 * dp[i - 1]) % MOD
            if S[i - 1] in last:
                dp[i] = (dp[i] - dp[last[S[i - 1]] - 1]) % MOD
            last[S[i - 1]] = i
        return (dp[n] - 1) % MOD
