class Solution:
    def sumOfDistancesInTree(self, N: int, edges: List[List[int]]) -> List[int]:
        adj = [[] for _ in range(N)]
        for u, v in edges:
            adj[u].append(v)
            adj[v].append(u)

        dist = [[0] * N for _ in range(N)]
        for i in range(N):
            dist[i][i] = 0
            q = [(i, 0)]
            visited = {i}
            while q:
                node, d = q.pop(0)
                for neighbor in adj[node]:
                    if neighbor not in visited:
                        dist[i][neighbor] = d + 1
                        visited.add(neighbor)
                        q.append((neighbor, d + 1))

        ans = [0] * N
        for i in range(N):
            for j in range(N):
                ans[i] += dist[i][j]

        return ans
