def whoIsWinner(moves, connect, size):
    """
    Determines the winner of a Connect Four game.

    Args:
        moves (list): A list of moves played in the game. Each move is a string
                      representing the column and player that made the move.
        connect (int): The number of consecutive pieces required to win.
        size (int): The size of the board (number of columns).

    Returns:
        str: The name of the winner ("Player R", "Player Y", "Player S", or "Draw")
             if a winner is found, or "Draw" if no winner is found.
    """

    board = [[None for _ in range(size)] for _ in range(size)]
    player_map = {
        "R": "Player R",
        "Y": "Player Y",
        "S": "Player S"
    }

    for move in moves:
        col = move[0]
        player = move[1]
        col_index = ord(col) - ord('A')
        row = size - 1
        while board[row][col_index] is not None and row >= 0:
            row -= 1
        board[row][col_index] = player

    # Check for horizontal wins
    for row in range(size):
        for col in range(size - connect + 1):
            color = board[row][col]
            if color is not None:
                win = True
                for i in range(1, connect):
                    if board[row][col + i] != color:
                        win = False
                        break
                if win:
                    return player_map[color]

    # Check for vertical wins
    for col in range(size):
        for row in range(size - connect + 1):
            color = board[row][col]
            if color is not None:
                win = True
                for i in range(1, connect):
                    if board[row + i][col] != color:
                        win = False
                        break
                if win:
                    return player_map[color]

    # Check for diagonal wins (top-left to bottom-right)
    for row in range(size - connect + 1):
        for col in range(size - connect + 1):
            color = board[row][col]
            if color is not None:
                win = True
                for i in range(1, connect):
                    if board[row + i][col + i] != color:
                        win = False
                        break
                if win:
                    return player_map[color]

    # Check for diagonal wins (top-right to bottom-left)
    for row in range(size - connect + 1):
        for col in range(connect - 1, size):
            color = board[row][col]
            if color is not None:
                win = True
                for i in range(1, connect):
                    if board[row + i][col - i] != color:
                        win = False
                        break
                if win:
                    return player_map[color]

    return "Draw"
