def whoIsWinner(moves, connect, size):
    """
    Determines the winner of a Connect Four game.

    Args:
        moves (list): A list of moves played in the game. Each move is a string
                      representing the column and player who made the move.
        connect (int): The number of consecutive pieces needed to win.
        size (int): The size of the board.

    Returns:
        str: The name of the player who won, or "Draw" if no winner is found.
    """
    board = [[None for _ in range(size)] for _ in range(size)]

    for move in moves:
        col, player_color = move.split('_')
        col = int(col) - 1
        player_color = player_color.upper()

        board[size - 1 - (col + 1)][-1] = player_color

    # Check rows
    for r in range(size):
        for c in range(size - connect + 1):
            color = board[r][c]
            if color is not None and all(board[r][c + i] == color for i in range(connect)):
                return color

    # Check columns
    for c in range(size):
        for r in range(size - connect + 1):
            color = board[r][c]
            if color is not None and all(board[r + i][c] == color for i in range(connect)):
                return color

    # Check diagonals (top-left to bottom-right)
    for r in range(size - connect + 1):
        for c in range(size - connect + 1):
            color = board[r][c]
            if color is not None and all(board[r + i][c + i] == color for i in range(connect)):
                return color

    # Check diagonals (top-right to bottom-left)
    for r in range(size - connect + 1):
        for c in range(connect - 1, size):
            color = board[r][c]
            if color is not None and all(board[r + i][c - i] == color for i in range(connect)):
                return color

    return "Draw"
