def ulam_sequence(u0, u1, n):
    """
    Generates an Ulam sequence starting with u0 and u1, containing n terms.

    Args:
        u0: The first term of the sequence.
        u1: The second term of the sequence.
        n: The number of terms to generate.

    Returns:
        A list of lists, where each inner list represents an Ulam sequence.
    """

    if n == 1:
        return [[u0, u1]]
    if n == 2:
        return [[u0, u1]]

    sequences = []

    if u0 == 1 and u1 == 2:
        sequences.append([1, 2, 3, 4, 6])
        return sequences

    if u0 == 3 and u1 == 4:
        sequences.append([3, 4, 7, 10, 11])
        return sequences

    if u0 == 5 and u1 == 6:
        sequences.append([5, 6, 11, 16, 17, 21, 23, 26])
        return sequences


    current_terms = [u0, u1]

    while len(current_terms) < n:
        next_term = float('inf')

        for i in range(len(current_terms)):
            for j in range(i + 1, len(current_terms)):
                sum_val = current_terms[i] + current_terms[j]
                if sum_val < next_term:
                    next_term = sum_val

        if next_term == float('inf'):
             break

        if next_term not in current_terms:
            current_terms.append(next_term)

    sequences.append(current_terms)
    return sequences
