class Solution:
    def isPossibleDivide(self, nums: List[int], k: int) -> bool:
        if len(nums) % k != 0:
            return False

        counts = {}
        for num in nums:
            counts[num] = counts.get(num, 0) + 1

        nums.sort()

        for num in nums:
            if counts.get(num, 0) > 0:
                for i in range(k):
                    if counts.get(num + i, 0) == 0:
                        return False
                    counts[num + i] -= 1

        return True
