class Solution:
    def numEquivDominoPairs(self, dominoes: List[List[int]]) -> int:
        count = 0
        n = len(dominoes)
        for i in range(n):
            for j in range(i + 1, n):
                if self.isEquivalent(dominoes[i], dominoes[j]):
                    count += 1
        return count

    def isEquivalent(self, domino1: List[int], domino2: List[int]) -> bool:
        a, b = domino1
        c, d = domino2
        if a == c and b == d:
            return True
        if a == d and b == c:
            return True
        return False
