def build_trie(*words):
    """
    Builds a trie data structure from a list of words.

    Args:
        *words: A variable number of strings representing the words to be added to the trie.

    Returns:
        A dictionary representing the trie.
    """
    trie = {}
    for word in words:
        current_node = trie
        for char in word:
            if char not in current_node:
                current_node[char] = {}
            current_node = current_node[char]
        current_node[word] = None  # Mark the end of the word
    return trie
