def isTree(matrix):
	"""
	Checks if a graph represented by an adjacency matrix is a tree.

	Args:
	matrix: A list of lists representing the adjacency matrix of the graph.

	Returns:
	True if the graph is a tree, False otherwise.
	"""
	n = len(matrix)
	if n == 0:
		return True  # Empty graph is considered a tree

	visited = [False] * n
	parent = [-1] * n

	def dfs(node):
		visited[node] = True
		for neighbor in matrix[node]:
			if not visited[neighbor]:
				parent[neighbor] = node
				if not dfs(neighbor):
					return False
			elif neighbor != parent[node]:
				return False
		return True

	if not dfs(0):
		return False

	for i in range(n):
		if not visited[i]:
			return False

	return True
