def solve():
    n, m, k = map(int, input().split())
    special_nodes = list(map(int, input().split()))
    edges = []
    for _ in range(m):
        x, y, z = map(int, input().split())
        edges.append((x, y, z))

    min_dist = float('inf')
    for i in range(k):
        for j in range(i + 1, k):
            node1 = special_nodes[i]
            node2 = special_nodes[j]

            dist = float('inf')

            import heapq
            graph = {node: [] for node in range(1, n + 1)}
            for x, y, z in edges:
                graph[x].append((y, z))
                graph[y].append((x, z))

            distq = [(0, node1)]
            visited = {node1}

            while distq:
                d, u = heapq.heappop(distq)

                if u == node2:
                    dist = d
                    break

                for v, weight in graph[u]:
                    if v not in visited:
                        visited.add(v)
                        heapq.heappush(distq, (d + weight, v))

            min_dist = min(min_dist, dist)

    print(min_dist)

