def solve(s):
    stack = []
    result = ""
    for char in s:
        if char == '(':
            stack.append(result)
            result = ""
        elif char == ')':
            result = stack.pop() + result
        elif char == '-':
            if len(result) > 0 and result[-1] == '(':
                stack.append(result)
                result = ""
            elif len(result) > 0 and result[-1] == '-':
                result = result[:-1]
            else:
                result += char
        else:
            result += char
    return result
