class Solution:
    def minCost(self, houses: List[int], cost: List[List[int]], m: int, n: int, target: int) -> int:
        """
        Finds the minimum cost of painting all the remaining houses in such a way that there are exactly target neighborhoods.

        Args:
            houses: A list of integers representing the color of each house.
            cost: A 2D list representing the cost of painting each house with each color.
            m: The number of houses.
            n: The number of colors.
            target: The desired number of neighborhoods.

        Returns:
            The minimum cost of painting all the remaining houses, or -1 if it is not possible.
        """

        dp = {}

        def solve(index, neighborhoods):
            if index == m:
                if neighborhoods == target:
                    return 0
                else:
                    return float('inf')

            if (index, neighborhoods) in dp:
                return dp[(index, neighborhoods)]

            min_cost = float('inf')

            # Option 1: Don't paint the current house
            if houses[index] != 0:
                min_cost = solve(index + 1, neighborhoods)
            else:
                # Option 2: Paint the current house with each color
                for color in range(1, n + 1):
                    new_neighborhoods = neighborhoods
                    if houses[index] == 0:
                        new_neighborhoods += 1
                    elif houses[index] != color:
                        new_neighborhoods += 1

                    min_cost = min(min_cost, cost[index][color - 1] + solve(index + 1, new_neighborhoods))

            dp[(index, neighborhoods)] = min_cost
            return min_cost

        result = solve(0, 0)

        if result == float('inf'):
            return -1
        else:
            return result
