class Solution:
    def pourWater(self, heights: List[int], V: int, K: int) -> List[int]:
        n = len(heights)
        result = heights[:]  # Create a copy to avoid modifying the original
        water = V
        while water > 0:
            max_height = 0
            max_index = -1
            for i in range(n):
                if i != K and heights[i] > max_height:
                    max_height = heights[i]
                    max_index = i

            if max_index == -1:
                break

            diff = max_height - heights[K]
            if diff > 0:
                water -= diff
                result[K] += diff
            else:
                break

        return result
