def diamond(n):
	if n <= 0 or n % 2 == 0:
		return None
	result = ""
	for i in range(1, n + 1, 2):
		spaces = " " * ((n - i) // 2)
		stars = "*" * i
		result += spaces + stars + "\n"
	for i in range(n - 2, 0, -2):
		spaces = " " * ((n - i) // 2)
		stars = "*" * i
		result += spaces + stars + "\n"
	return result.rstrip()
