class Solution:
    def lengthLongestPath(self, input: str) -> int:
        stack = [0]
        max_len = 0
        for line in input.splitlines():
            name = line.lstrip('\t')
            depth = len(line) - len(name)
            if '.' in name:
                max_len = max(max_len, stack[depth] + len(name))
                stack.append(stack[depth] + len(name) + 1)
            else:
                stack.append(stack[depth])
        return max_len
