def word_wrap(text, limit):
	words = text.split()
	lines = []
	current_line = ""
	for word in words:
		if len(current_line) == 0:
			current_line = word
		elif len(current_line) + len(word) + 1 <= limit:
			current_line += " " + word
		else:
			lines.append(current_line)
			current_line = word
	if len(current_line) > 0:
		lines.append(current_line)
	result = "--".join(lines)
	return result
