def update_score(current_score, called_trump, alone, tricks):
    """
    Calculates the new score after finishing a hand of euchre.

    Args:
        current_score: A list representing the current score of the two teams.
        called_trump: An integer (1 or 2) indicating which team called trump.
        alone: A boolean indicating whether the team that called trump went alone.
        tricks: A list of integers representing the number of tricks won by each team.

    Returns:
        A list representing the updated score of the two teams.
    """
    team1_score = current_score[0]
    team2_score = current_score[1]

    if called_trump == 1:
        if tricks[0] >= 2:
            if alone:
                team2_score += 4
            else:
                team2_score += 2
        elif tricks[0] >= 3 and tricks[0] <= 4:
            team1_score += 1
        elif tricks[0] == 5:
            team1_score += 2
    else:
        if tricks[1] >= 2:
            if alone:
                team1_score += 4
            else:
                team1_score += 2
        elif tricks[1] >= 3 and tricks[1] <= 4:
            team2_score += 1
        elif tricks[1] == 5:
            team2_score += 2

    return [team1_score, team2_score]
