class Solution:
    def findNumOfValidWords(self, words: List[str], puzzles: List[str]) -> List[int]:
        """
        Given a list of words and a list of puzzles, return an array answer
        where answer[i] is the number of words in the given word list words that
        are valid with respect to the puzzle puzzles[i].
        """
        ans = []
        for puzzle in puzzles:
            count = 0
            for word in words:
                if len(word) == len(puzzle):
                    if word[0] == puzzle[0]:
                        valid = True
                        for i in range(len(word)):
                            if word[i] not in puzzle:
                                valid = False
                                break
                        if valid:
                            count += 1
            ans.append(count)
        return ans
