def handle(func, success, failure, *exceptions):
    """
    Handles exceptions raised by a function without using try/except.

    Args:
        func: The function to execute.
        success: A callable to be called with the function's return value.
        failure: A callable to be called with the exception raised.
        *exceptions: A variable number of exception types to catch.
    """
    try:
        result = func()
        success(func, result)
    except Exception as e:
        if isinstance(e, exceptions):
            failure(func, e)
        else:
            pass  # Let the exception propagate - don't catch it

