class Solution:
    def getHint(self, secret: str, guess: str) -> str:
        """
        Given the secret number and the friend's guess, return a hint
        according to the secret number and friend's guess.

        Args:
            secret: The secret number.
            guess: The friend's guess.

        Returns:
            A string indicating the number of bulls and cows.
        """
        bulls = 0
        cows = 0
        secret_counts = {}
        guess_counts = {}

        for i in range(len(secret)):
            if secret[i] == guess[i]:
                bulls += 1
            else:
                secret_counts[secret[i]] = secret_counts.get(secret[i], 0) + 1
                guess_counts[guess[i]] = guess_counts.get(guess[i], 0) + 1

        for digit, count in guess_counts.items():
            if digit in secret_counts:
                cows += min(count, secret_counts[digit])

        return str(bulls) + "A" + str(cows) + "B"
