class Solution:
    def canConvertString(self, s: str, t: str, k: int) -> bool:
        if len(s) != len(t):
            return False

        shifts = {}
        for i in range(len(s)):
            diff = ord(t[i]) - ord(s[i])
            if diff != 0:
                if diff not in shifts:
                    shifts[diff] = 0
                shifts[diff] += 1

        if k >= 1000:
            return True

        for shift in shifts:
            if shift == 0:
                continue
            if shift % 2 == 0:
                if k < shifts[shift] * 2:
                    return False
            else:
                if k < shifts[shift] * 2 + 1:
                    return False

        return True
