class Solution:
    def numTilePossibilities(self, tiles: str) -> int:
        tiles = sorted(list(tiles))
        memo = {}

        def backtrack(index):
            if index == len(tiles):
                return 1

            if index in memo:
                return memo[index]

            count = 0
            for i in range(index, len(tiles)):
                if i > index and tiles[i] == tiles[i - 1]:
                    continue
                count += backtrack(i + 1)

            memo[index] = count
            return count

        return backtrack(0)
