    def minCost(self, n: int, cuts: List[int]) -> int:
        cuts = [0] + sorted(cuts) + [n]
        k = len(cuts)
        dp = [[0] * k for _ in range(k)]

        for length in range(2, k):
            for i in range(k - length):
                j = i + length
                dp[i][j] = float('inf')
                for m in range(i + 1, j):
