	words = text.split()
	lines = []
	current_line = []
	current_line_length = 0

	for word in words:
		if current_line_length + len(word) + len(current_line) <= width:
			current_line.append(word)
			current_line_length += len(word)
		else:
			lines.append(" ".join(current_line))
			current_line = [word]
			current_line_length = len(word)

	if current_line:
		lines.append(" ".join(current_line))

	if len(lines) == 1:
		return [lines[0]]
	else:
		result = []
		for line in lines:
			spaces_needed = width - current_line_length
			space_between = spaces_needed // (len(current_line) - 1)
			extra_spaces = spaces_needed % (len(current_line) - 1)
			formatted_line = ""
			for i, word in enumerate(current_line):
				formatted_line += word
				if i < len(current_line) - 1:
					formatted_line += " " * space_between
					if i < extra_spaces:
						formatted_line += " "
			result.append(formatted_line)
		return result
