class Solution:
    def smallestDivisor(self, nums: List[int], threshold: int) -> int:
        left, right = 1, max(nums)
        ans = right
        while left <= right:
            mid = (left + right) // 2
            total = 0
            for num in nums:
                total += (num + mid - 1) // mid
            if total <= threshold:
                ans = mid
                right = mid - 1
            else:
                left = mid + 1
        return ans
