                all_lower = False
                break
        if all_lower:
            return True

        # Case 3: Only the first letter is capital
        first_cap = True
        if not 'A' <= word[0] <= 'Z':
            first_cap = False
        else:
            for i in range(1, n):
                if not 'a' <= word[i] <= 'z':
                    first_cap = False
                    break

        if first_cap:
            return True

        return False
