def calculate_doors_open(n, a, d, clients):
    doors_open = 0
    last_open = 0
    for i in range(1, n + 1):
        next_employee = i * a
        if next_employee < last_open:
            continue
        if next_employee - last_open > d:
            doors_open += 1
            last_open = next_employee
        else:
            last_open = max(last_open, next_employee)
    for client in clients:
        if client < last_open:
            continue
        if client - last_open > d:
            doors_open += 1
            last_open = client
        else:
