def fight(robot_1, robot_2, tactics):
    # Determine which robot attacks first
    if robot_1.speed > robot_2.speed:
        attacker, defender = robot_1, robot_2
    elif robot_1.speed < robot_2.speed:
        attacker, defender = robot_2, robot_1
    else:
