    rooms = SegmentTree(K)
    rooms.build(1, 0, K - 1)
    count = 0
    for start, end, room in events:
        max_end = rooms.query(1, 0, K - 1, 0, room - 1)
        if max_end <= start:
            count += 1
            rooms.update(1, 0, K - 1, room - 1, end)
    return count


