import math
from collections import defaultdict

def prime_factors(n):
    factors = defaultdict(int)
    while n % 2 == 0:
        factors[2] += 1
        n //= 2
    for i in range(3, int(math.sqrt(n)) + 1, 2):
        while n % i == 0:
            factors[i] += 1
            n //= i
    if n > 2:
        factors[n] += 1
    return factors

def generate_X(Y):
    factors = {y: prime_factors(y) for y in Y}
    X = set()
    for y in sorted(Y, reverse=True):
        factors_of_y = factors[y]
        max_factor = max(factors_of_y.keys())
        for i in range(factors_of_y[max_factor], -1, -1):
            x = max_factor ** i
            while x <= y:
                if x not in X:
                    X.add(x)
                x *= 2
    return sorted(X)

def main():
