from collections import deque
from typing import List

class Solution:
    def pourWater(self, heights: List[int], V: int, K: int) -> List[int]:
        n = len(heights)
        water = [0] * n

        # Pour water at index K
        water[K] += 1
        V -= 1

        # Simulate water flow from K to both sides
        queue = deque([K])
        while V > 0:
            while queue and water[queue[0]] > 0:
                queue.popleft()

            # Move left
            while queue and (queue[0] == 0 or heights[queue[0] - 1] >= heights[queue[0]]):
                left = queue.popleft()
                if left > 0 and water[left] > 0:
                    water[left] -= 1
                    if left > 0:
                        water[left - 1] += 1
                        V -= 1
                        if V == 0:
                            return water
                        queue.append(left - 1)
                if left < n - 1 and water[left + 1] > 0:
                    water[left + 1] -= 1
                    if left + 1 < n:
                        water[left + 2] += 1
                        V -= 1
                        if V == 0:
                            return water
                        queue.append(left + 2)
                if left > 0 and water[left] > 0:
                    queue.append(left)
                    break

            # Move right
            while queue and (queue[-1] == n - 1 or heights[queue[-1] + 1] >= heights[queue[-1]]):
                right = queue.pop()
                if right < n - 1 and water[right] > 0:
                    water[right] -= 1
                    if right < n - 1:
                        water[right + 1] += 1
                        V -= 1
                        if V == 0:
                            return water
                        queue.appendleft(right + 1)
                if right > 0 and water[right - 1] > 0:
                    water[right - 1] -= 1
                    if right - 1 > 0:
                        water[right - 2] += 1
                        V -= 1
                        if V == 0:
                            return water
                        queue.appendleft(right - 2)
                if right < n - 1 and water[right] > 0:
                    queue.append(right)
                    break

            # Add new water at K
            if water[K] > 0:
                water[K] -= 1
                V -= 1
                queue.append(K)

        return water
```

