        y_root = self.find(y)
        if x_root == y_root:
            return
        if self.rank[x_root] < self.rank[y_root]:
            self.parent[x_root] = y_root
        elif self.rank[x_root] > self.rank[y_root]:
            self.parent[y_root] = x_root
        else:
            self.parent[y_root] = x_root
            self.rank[x_root] += 1

def kruskal(graph, n):
    edges = []
    for i in range(n):
        for j in range(i + 1, n):
            if graph[i][j] > 0:
                edges.append((graph[i][j], i, j))
    edges.sort(reverse=True)
