import math
import sys

def get_eulerian_path(s):
    n = len(s)
    deg = [0] * n
    for i in range(n):
        deg[i] = 1 if s[i] == '1' else 2
    if sum(deg) % 2!= 0 or max(deg) >= 3:
        return []

    path = [0] * n
    used = [False] * n
    used[0] = True
    path[0] = 0
    i = 1
    while i < n:
        if s[path[i-1]] == '1':
            if path[i-1] + 1 < n and not used[path[i-1] + 1]:
                path[i] = path[i-1] + 1
                used[path[i]] = True
                i += 1
            elif i + 1 < n and not used[i + 1]:
                path[i] = i + 1
                used[path[i]] = True
                i += 1
            else:
                return []
        else:
            if path[i-1] + 2 < n and not used[path[i-1] + 2]:
                path[i] = path[i-1] + 2
                used[path[i]] = True
                i += 1
            elif i + 2 < n and not used[i + 2]:
                path[i] = i + 2
                used[path[i]] = True
                i += 1
            else:
                return []

